(function() {
  var grunt, helpers, path, xmlParser;

  grunt = require('grunt');

  xmlParser = require('xml2json');

  path = require('path');

  helpers = require(path.join(__dirname, '..', '..', '..', 'tasks', 'helpers'))(grunt);

  if (helpers.canBuild('android')) {
    exports.phonegap = {
      'debuggable in AndroidManifest.xml should match config.debuggable': function(test) {
        var debuggable, manifest, xml;
        test.expect(1);
        debuggable = grunt.config.get('phonegap.config.debuggable');
        if (grunt.util.kindOf(debuggable) === 'function') {
          debuggable = debuggable();
        }
        xml = grunt.file.read('test/phonegap/platforms/android/AndroidManifest.xml');
        manifest = xmlParser.toJson(xml, {
          object: true
        });
        test.equal(debuggable, manifest['manifest']['application']['android:debuggable'], 'debuggable value should match');
        return test.done();
      }
    };
  }

}).call(this);
