(function() {
  var grunt, helpers, path, xmlParser;

  grunt = require('grunt');

  xmlParser = require('xml2json');

  path = require('path');

  helpers = require(path.join(__dirname, '..', '..', '..', 'tasks', 'helpers'))(grunt);

  if (helpers.canBuild('android')) {
    exports.phonegap = {
      '<application ?param?> in AndroidManifest.xml should match config.androidManifest': function(test) {
        var androidManifest, data, key, manifest, value, xml, _ref, _ref1;
        test.expect(2);
        data = grunt.config.get('phonegap.config.androidManifest');
        if (grunt.util.kindOf(data) === 'function') {
          androidManifest = data();
        } else {
          androidManifest = data;
        }
        xml = grunt.file.read('test/phonegap/platforms/android/AndroidManifest.xml');
        manifest = xmlParser.toJson(xml, {
          object: true
        });
        _ref = androidManifest.application;
        for (key in _ref) {
          value = _ref[key];
          test.equal(value, manifest['manifest']['application'][key], 'application ' + key + ' value should match');
        }
        _ref1 = androidManifest.activity;
        for (key in _ref1) {
          value = _ref1[key];
          test.equal(value, manifest['manifest']['application']['activity'][key], 'activity ' + key + ' value should match');
        }
        return test.done();
      }
    };
  }

}).call(this);
